import { URL } from "../url";

describe("URL", () => {
  test("constructor", () => {
    let url = new URL("https://fastly.com");
    expect(url).toBeTruthy();
  });

  test("constructor with relative url", () => {
    let url = new URL("./some/path", "https://fastly.com");
    expect(url).toBeTruthy();
  });
});
