export class URLProperties {
  protocol: string = "";
  username: string = "";
  password: string = "";
  hostname: string = "";
  port: string = "";
  pathname: string = "";
  search: string = "";
  hash: string = "";

  toString(): string {
    let response = "";

    response += this.protocol;
    response += "//";
    response += this.hostname;
    response += this.pathname;
    response += this.search;
    response += this.hash;

    return response;
  }
}
